import React from 'react'
import { useState } from 'react'
import { useRef } from 'react'

const App = () => {

  const inp = useRef(null)
const [show,setshow] = useState(false)
 const submit = e => {
  e.preventDefault()
  const name =inp.current.value
  setshow(true)

 }
  return (
    <div className='app'>
      <form
      onSubmit={submit}
        className='form_css'>
        <input
          className='inp_css'
          id='inp'
          ref={inp}
        />

        <button
          className='btn_css'

          type='submit'>
          Submit
        </button>
   
         {
          show ? <h3> {inp.current.value} </h3> : ''
         }
       
      </form>
    </div>
  )
}

export default App
