import React, { useReducer } from 'react'
import './index.css'

const init_val = 0;

const reducer = (state,action) => {
switch (action.type) {
  case 'increase': return state += 1

  case 'decrease': return state > 1 ? state -=1 : state =0

  case 'reset' :  return state = 0
  
    
    

  

  default: return state
    break;
}
}

const App = () => {

  const [state, dispatch] = useReducer(reducer, init_val )

  return (
    <div className='app'>
      <h2> {state} </h2>
      <button className='btn_css' onClick={()=>dispatch({type:'increase'})}  > Increment </button>
      <button className='btn_css' onClick={()=>dispatch({type:'decrease'})}  > Decrement </button>
      <button className='btn_css' onClick={()=>dispatch({type:'reset'})} > Reset </button>
    </div>
  )
}

export default App
