import './index.css'

const App = () => {
const [reSize,setResize]= useState()
useEffect(() => {
  window.addEventListener('resize',()=>{
    setResize(window.innerWidth)
  })

  return () => {
  }
}, [reSize])

  return (
    <div className='app'>
       <h1 className='heading'> Window Length</h1>
      <h1 className='heading'>{reSize} </h1>
    
    </div>
  )
}

export default App
