import React,{useState,useEffect} from 'react'
import { AiOutlineAppstoreAdd } from 'react-icons/ai'
import { nanoid } from 'nanoid'
const Todo = () => {

const [inp, setinp] = useState('')
const [item, setitem] = useState([])

useEffect(() => {
 const fetched = JSON.parse(localStorage.getItem('TODO'))   
 setitem(fetched)
}, [])


const Submit = (e) => {
e.preventDefault()
const newitem = { id:nanoid(), thing:inp}
const item_added = [...item,newitem]
localStorage.setItem('TODO', JSON.stringify(item_added))
setitem(item_added)
setinp('')
}










    return (
        <div className='todo'>

            <h3 className='heading'>
                Add Your Item Here
            </h3>

            <div className="form">
                <input
                    type="text"
                    value={inp}
                    onChange={(e)=> setinp(e.target.value) }
                    placeholder='Add item ...'
                    className='inp_css'
                />
                <AiOutlineAppstoreAdd
                    className='icon_css'
                    onClick={Submit}
                />
            </div>

            {
                item.map((elem)=>{
                    return  <div className="list" key={elem.id}>
                    <span className='list_item'> {elem.thing} </span>
                    <AiOutlineAppstoreAdd className='delete_icon'/>
                  </div>
      
                })
            }

            
           


        </div>
    )
}

export default Todo
