import React,{useReducer} from 'react'

const reducer = (state,action) => {
    switch (action.type) {
        case 'inp_email': return { ...state, email:action.load_email}
            
           
    
        default: return state
        
    }
}


const Reducer = () => {

const [state, dispatch] = useReducer( reducer, { email:'',pwd:''})




  return (
    <div>
        < input
        className='inp_css'
        onChange={(e)=>dispatch({type:'inp_email', load_email:e.target.value})}
        value={state.email}
        type="email"   />
        < input
        className='inp_css'
        onChange={(e)=>dispatch({type:'inp_pwd',load_pwd:e.target.value})}
        value={state.pwd}
        type="password" />
  <h2>
    {state.email}
  </h2>

    </div>
  )
}

export default Reducer
