import React, { useState } from 'react'
import Data from './Data'
const Table = () => {
 
    const [user,setuser] = useState(Data)
    const [age,setage] = useState()

    const Filtering = (gend) =>{
       const filtered = Data.filter((fil_item)=>
       fil_item.gender === gend
       )
       setuser(filtered)
       setage(filtered)
    }

    const Aging = age_arg =>{
    const age_filter= age.filter((item)=>
        item.age < age_arg
    )
    }

  return (
    <div>

<div className='btn_group'>
    <button onClick={()=> setuser(Data)}> All</button>
    <button  onClick={()=>{Filtering('male')}}> Male</button>
    <button  onClick={()=>{Filtering('female')}}> Female</button>
</div>

<div className='btn_group'>
    <button onClick={()=>{Aging(20)}}> All</button>
    <button  onClick={()=>{Aging(40)}}> Male</button>
    <button  onClick={()=>{Aging(40)}}> Female</button>
</div>

        <table>
            <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Gender</th>
                <th>Age</th>
            </tr>
        {
            user.map((item)=>{
                return <tr key={item.id}>
                    <td> {item.name} </td>
                    <td> {item.email} </td>
                    <td> {item.gender} </td>
                    <td> {item.age} </td>
                </tr>
            })
        }
        </table>
    </div>
  )
}

export default Table
