import React,{useReducer} from 'react'


const reducer = ( state, action) => {
switch (action.type) {
  case 'increment': return {count:state.count+1}
  case 'decrement': return state.count > 1 ? {count:state.count-1} : {count:state.count =0}
    


  default: return state

}
}


const App = () => {

  const [state, dispatch] = useReducer(reducer, { count : 0, input:'', mode:false}  )


  return (
    <div className='app'>
      
      <h1>{state.count}</h1>

      <button className='btn_css' onClick={()=>dispatch({type:'increment'})} >Increase</button>
      <button className='btn_css' onClick={()=>dispatch({type:'decrement'})} >Decrease</button>

    </div>
  )
}

export default App
