import React, { useReducer } from 'react'
import './index.css'


const inital_value = 0;

const reducer = (state, action) => {

  // console.log(state,action)

  switch (action.type) {
    case 'increase':
      return state += 1

    case 'decrease':
      if (state > 1) {
        return state -= 1;
      } else {
        return state = 0;
      }
     
      case 'reset' : 
     return state = 0
    

    default:
      return state;
  }

}


const App = () => {

  const [state, dispatch] = useReducer(reducer, inital_value)

  return (
    <div className='app'>
      <h2> {state} </h2>
      <button className='btn_css' onClick={() => dispatch({ type: 'increase' })} > Increment </button>
      <button className='btn_css' onClick={() => dispatch({ type: 'decrease' })}  > Decrement </button>
      <button className='btn_css' onClick={() => dispatch({ type: 'reset' })}  > Reset </button>
    </div>
  )
}

export default App
