import React from 'react'

const Tab = ({ mapping , search}) => {


const keys = ['name','email']





    return (
        <div>
            <table className='table'>
            
                    <th>

                        <td>Name</td>
                        <td>Email</td>
                    </th>


                   {
                    mapping.filter((item) =>
                    keys.some((elem)=> item[elem].toLowerCase().includes(search) )

                //    item.name.toLowerCase().includes(search) ||
                //    item.email.toLowerCase().includes(search) 
                    )
                    .map(tab =>{
                        return   <tr key={tab.id} >
                        <td> {tab.name} </td>
                        <td> {tab.email} </td>
                    </tr>
                    })
                   }



                
            </table>
        </div>
    )
}

export default Tab
