import React, { useReducer } from 'react'




const reducer = (type,action) => {

  switch (action.type) {
    case 'handle_email': return {...state , email: action.email_load }
    case 'handle_email': return {...state , email: action.email_load } 
    default:return state
  
  }

}

 

const App = () => {

const [state, dispatch] = useReducer(reducer,{email:'', pwd:''})

  return (
    <div className={`app`}>

      <input
        className='inp_css'
        value={state.email}
        onChange={() => dispatch({type:'handle_email'})}
        placeholder='Enter Email'
        type='email'
      />

      <input
        className='inp_css'
        value={state.pwd}
        onChange={() => dispatch({type:'handle_pwd'})}
        placeholder='Enter Password'
        type='password'
      />


    </div>
  )
}

export default App
