import React, { useState } from 'react'
import Data from './Data'
const Table = () => {
 
    const [user,setuser] = useState(Data)
    const [age,setage] = useState([])

    const Filtering = (gend) =>{
       const filtered = Data.filter((fil_item)=>
       fil_item.gender === gend
       )
       setuser(filtered)
       setage(filtered)
    }

    const Aging20 = age_arg =>{
    const age_filter= age.filter((item)=>
        item.age <= age_arg  
        ) 
    setuser(age_filter)
    }
    const Aging40 = age_arg =>{
        const age_filter= age.filter((item)=>
            item.age <= age_arg   

            ) 
        setuser(age_filter)
        }

  return (
    <div>

<div className='btn_group'>
    <button onClick={()=> setuser(Data)}> All</button>
    <button  onClick={()=>{Filtering('male')}}> Male</button>
    <button  onClick={()=>{Filtering('female')}}> Female</button>
</div>

<div className='btn_group'>
    <button onClick={()=>{Aging20(20)}}> Less Than 20</button>
    <button  onClick={()=>{Aging40(40)}}> 20 to 40 </button>
    <button  onClick={()=>{}}> Greater Than 40</button>
</div>

        <table>
            <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Gender</th>
                <th>Age</th>
            </tr>
        {
            user.map((item)=>{
                return <tr key={item.id}>
                    <td> {item.name} </td>
                    <td> {item.email} </td>
                    <td> {item.gender} </td>
                    <td> {item.age} </td>
                </tr>
            })
        }
        </table>
    </div>
  )
}

export default Table
