import React, { useState } from 'react'
import Data from './Data'
import Genger from './Genger'
import Age from './Age'

const Table = () => {
 
    const [user,setuser] = useState(Data)
    const [age,setage ] = useState([])

    

 

    const Aging40 = age_arg =>{
        const age_filter= age.filter((item)=>
            item.age <= age_arg   &&
            item.age > 20
            ) 
        setuser(age_filter)
        }
        const Aging60 = age_arg =>{
            const age_filter= age.filter((item)=>
                item.age > age_arg  
              
                ) 
            setuser(age_filter)
            }

  return (
    <div>

<Genger setuser={setuser}  setage={setage}/>
<Age setuser={setuser} />




        <table>
            <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Gender</th>
                <th>Age</th>
            </tr>
        {
            user.map((item)=>{
                return <tr key={item.id}>
                    <td> {item.name} </td>
                    <td> {item.email} </td>
                    <td> {item.gender} </td>
                    <td> {item.age} </td>
                </tr>
            })
        }
        </table>
    </div>
  )
}

export default Table
