import React,{useState} from 'react'
import { AiOutlineAppstoreAdd } from 'react-icons/ai'
import { nanoid } from 'nanoid'
const Todo = () => {

const [inp, setinp] = useState('')
const [item, setitem] = useState([])

const Submit = (e) => {
e.preventDefault()
const newitem = { id:nanoid(), thing:inp}
const item_added = [...item,newitem]
setinp(item_added)
}










    return (
        <div className='todo'>

            <h3 className='heading'>
                Add Your Item Here
            </h3>

            <div className="form">
                <input
                    type="text"
                    value={inp}
                    onChange={(e)=> setinp(e.target.value) }
                    placeholder='Add item ...'
                    className='inp_css'
                />
                <AiOutlineAppstoreAdd
                    className='icon_css'
                    onClick={Submit}
                />
            </div>

            <div className="list">
              <span className='list_item'> apple </span>
              <AiOutlineAppstoreAdd className='delete_icon'/>
            </div>


        </div>
    )
}

export default Todo
