const object = 
[{id: Date.now(),email:'adil@gmail.com',password:'12345'}]

const reducer = (state,action) => {

}

const Reducer = () => {

    const [inp, setinp] = useState({email:'',pwd:''})
    const [state, dispatch] = useReducer(reducer, object )

    const Input = e => {
const {name,value} = e.target
setinp({...inp,[name]:value})

    }

    const  Submit = e => {

    }

    return (
        <div>

            <form action="" onSubmit={Submit}>
                <input
                    name='email'
                    value={inp.email}
                    onChange={Input}
                    className='inp_css'
                    placeholder='Enter Email'
                    type="email"
                />
                <input
                    name='pwd'
                    value={inp.pwd}
                    onChange={Input}
                    className='inp_css'
                    placeholder='Enter Password'
                    type="password"
                />
                <input
                    className='btn_css'
                    type="submit"
                />

            </form>

            <table>
  <tr>
    <th>Email</th>
    <th>Password</th>
    <th>Delete</th>
  </tr>


  {
    state.map((elem)=> {
        return   <tr key={elem.id} >
        <td> {elem.email} </td>
        <td> {elem.password} </td>
        <td><button 
      
        className='btn_css tab_btn'
        > Delete</button></td> 
      </tr>
    }
    
    )
  }
 
 
 
</table>

        </div>
    )
}

export default Reducer
