import React from 'react'
import {FcDeleteDatabase,FcEditImage} from 'react-icons/fc'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
 
const List = ({items,removeitem,edititem}) => {

  const Remove = () =>{
    removeitem(items.id)
    toast.success('Item Deleted Successfully !', {
      position: "top-center",});
  }

  const Edit = () =>{
    edititem(items.id)
  }

  return (
    <div className='list'>
      <table>
  <tr>
    <td className='td1'> {items.thing}  </td>
    <td className='td2' >
      <FcEditImage onClick={Edit} />
    </td>
    <td className='td2' >
      <FcDeleteDatabase title='remove' onClick={Remove}/>
    </td>
   
  </tr>
</table>

<ToastContainer/>
    </div>
  )
}

export default List
