import React from 'react'

const Age = ({setuser,age}) => {

    const Aging20 = age_arg =>{
        const age_filter= age.filter((item)=>
            item.age <= age_arg  
            ) 
        setuser(age_filter)
        }

        const Aging40 = age_arg =>{
            const age_filter= age.filter((item)=>
                item.age <= age_arg   &&
                item.age > 20
                ) 
            setuser(age_filter)
            }

            const Aging60 = age_arg =>{
                const age_filter= age.filter((item)=>
                    item.age > age_arg  
                  
                    ) 
                setuser(age_filter)
                }




  return (
    <div>
        <div className='btn_group'>
    <button onClick={()=>{Aging20(20)}}> Less Than 20</button>
    <button  onClick={()=>{Aging40(40)}}> 20 to 40 </button>
    <button  onClick={()=>{Aging60(40)}}> Greater Than 40</button>
</div>
    </div>
  )
}

export default Age
