import React from 'react'
import { useState } from 'react'
import { useRef } from 'react'

const App = () => {

  const data = useRef(null)
  const [show, setshow] = useState(false)
  const submit = e => {
e.preventDefault()

const name = data.current.value
  }

 
  return (
    <div className='app'>
      <form
      onSubmit={submit}
        className='form_css'>
        <input
          className='inp_css'
          id='data'
          ref={data}
        />

        <button
          className='btn_css'

          type='submit'>
          Submit
        </button>
   
         {
          show ? <h3> {data.current.value} </h3> : ''
         }
       
      </form>
    </div>
  )
}

export default App
