import React, { useState } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {list} from './Pics/todo.png'

const Form = ({ additem }) => {

  const [inp, setinp] = useState('')

  const Submit = e => {
    e.preventDefault()
    if (inp) {
      additem(inp)
      setinp('')

      toast.success('Item Added Successfully !', {
        position: "top-center",
      });

    } else {

      toast.error('Please Add Item !', {
        position: "top-center",
      });

    }
  }


  return (
    <div
      className='form' >
<img src={list} alt="" />
    <h1 className='header'> Add Your Item Here</h1>

      <form
        action=""
        onSubmit={Submit}
      >
        <input type="text"
          value={inp}
          onChange={(e) => setinp(e.target.value)}
          placeholder='Add Item ...'
          className='inp_css'
        />

        <input type="submit"
          className='btn_css'
        />

      </form>
      <ToastContainer />
    </div>
  )
}

export default Form
