import React, { useReducer } from 'react'
import './index.css'

const object = {
  count : 0,
}

const reducer = (state,action) => {

  switch (action.type) {
    case 'increase': return { count:state.count +=1}
      
    
  
    default:
      break;
  }



}

const App = () => {

  const [state, dispatch] = useReducer(reducer, object)

  return (
    <div className='app'>
 
       <h2> {state.count} </h2>
      
      <input type='text' className='inp_css' />

      <button className='btn_css' onClick={() => dispatch({ type: 'increase' })}  > Increment </button>
      <button className='btn_css' onClick={() => dispatch({ type: 'decrease' })}  > Decrement </button>
      <button className='btn_css' onClick={() => dispatch({ type: 'color' })} > Reset </button>
    </div>
  )
}

export default App
