import React,{useState,useEffect} from 'react'

const App = () => {

  const [num, setnum] = useState(0)

  useEffect(() => {

    if (num>=1) {
      document.title=`chats (${num})`
    } else {
      document.title=`chats`

    }
     
    
  }, [num])
  

  return (
    <div className='app'>

<h1>{num}</h1>
<button
className='btn_css'
 onClick={()=>setnum(num+1) }>
  Increase
</button>

    </div>
  )
}

export default App
