import React, { useEffect } from 'react'
import { useState } from 'react'
import { nanoid } from 'nanoid'
import { AiOutlineDelete } from 'react-icons/ai'
import './index.css'
const App = () => {

  const Array = [
    // { id: nanoid(), name: 'Adil', gender: 'male' },
    // { id: nanoid(), name: 'Inam', gender: 'male' },
    // { id: nanoid(), name: 'Dawood', gender: 'male' },
    // { id: nanoid(), name: 'Neelam', gender: 'female' },
    // { id: nanoid(), name: 'Kiran', gender: 'female' },
  ]

  const [isArray, setArray] = useState(Array)

  const [val, setVal] = useState({
    fname: '', gender: ''
  })

  // input handling

  const Input = (e) => {
    const { name, value } = e.target
    setVal({ ...val, [name]: value })
  }

  // deleting array data

  const Delete = (id) => {
    const delete_item = isArray.filter(item => item.id !== id)
    setArray(delete_item)
  }

  // fetching data from localStorage 

  useEffect(()=>{
    const fetch = JSON.parse(localStorage.getItem('to_do'))
    if(fetch){
      setArray(fetch)
    }
  },[])

  // adding new data 

  const Submit = e => {
    e.preventDefault()
    const obj = { id: nanoid(), name: val.fname, gender: val.gender }
    const newdata = [...isArray, obj]
    localStorage.setItem('to_do', JSON.stringify(newdata))
    setArray(newdata)
  }

  return (
    <div className='app'>
      <div className='table'>

        <form className='form' onSubmit={Submit}>
          <input className='input_css'
            value={val.fname} name='fname'
            onChange={Input}
            type='text' placeholder='enter name' />
          <input className='input_css'
            value={val.gender} name='gender'
            onChange={Input}
            type='text' placeholder='enter gender' />
          <input className='input_css'
            type='submit' />
        </form>

        <table>
          <tr>
            <th>Name</th>
            <th>Gender</th>
            <th>Delete</th>
          </tr>
          {
            isArray.map((item) =>
              <tr key={item.id}>
                <td> {item.name} </td>
                <td> {item.gender} </td>
                <td> < AiOutlineDelete className='icon' onClick={() => Delete(item.id)} /> </td>
              </tr>)
          }


        </table>

      </div>
    </div>
  )
}

export default App
