import React, { useState, useEffect } from 'react'
import './index.css'
const App = () => {

  const [count, setcount] = useState(0)

  useEffect(() => {
if (count>=1) {
  document.title = `Chats(${count})`
} else {
  document.title='Chats'
}

   


  }, [count])



  return (

    <div className='app'>

      <h1 className='heading'>
        {count}
      </h1>
      <button
onClick={()=>{ setcount(count + 1) }}
        className='btn_css'>
        Increase
      </button>

    </div>
  )
}

export default App
