import { ToastContainer, toast } from 'react-toastify';

const Form = ({additem}) => {

  const [inp, setinp] = useState('')

  const Submit = e => {
    e.preventDefault()
   if (inp) {
    additem(inp)
    setinp('')
    toast.success('ðŸ¦„ Wow so easy!', {
      position: "top-center",
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      });
   } else {
   
   }}


  return (
    <div
    className='form' >

<form 
action=""
onSubmit={Submit}
>

    <input type="text"
    value={inp}
    onChange={(e)=> setinp(e.target.value) }
     placeholder='Add Item ...'
      className='inp_css'
       />

    <input type="submit" 
     className='btn_css'
    />
<ToastContainer />
</form>
    </div>
  )
}

export default Form
