import React, { useState, useEffect } from 'react'
import './index.css'
const App = () => {

  const [count, setcount] = useState()

  useEffect(() => {
  
    window.addEventListener('resize',()=>{
      setcount(window.innerWidth)
    })
  
    return () => {
      window.removeEventListener('resize',()=>{
        setcount(window.innerWidth)
      })
    }
  }, [count])
  

  return (

    <div className='app'>

      <h1
        className='heading'>
        {count}
      </h1>

      <button
       
        className='btn'>
        Increment
      </button>

    </div>
  )
}

export default App
