import React,{useState} from 'react'
import Form from './Form'
import List from './List'
import { nanoid } from 'nanoid'
const App = () => {

const Array = [
  // {id:nanoid(),thing:'Banana'}
]

  const [data, setdata] = useState(Array)

  const additem = (inp) => {
  
    const newitem = {id:nanoid(),thing:inp}
    const newthing = [...data,newitem]

    setdata(newthing)
  }

  const removeitem = (id) => {

  const removed=data.filter((fil)=>  fil.id !==id )
setdata(removed)

  }


  return (
    <div className='app'>
      <Form  additem={additem} />
    
      {
             data.map((elem)=>{
              return   <List 
              key={elem.id}
              items={elem}
              removeitem={removeitem}
              />
             })
      }
    </div>
  )
}

export default App
