import React from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import list from './Pics/todo.png'
import {FcAddDatabase,FcEditImage} from 'react-icons/fc'

const Form = ({ additem,inp,setinp,toggle }) => {

 

  const Submit = e => {
    e.preventDefault()
    if (inp) {
      additem(inp)
      setinp('')

    } else {

      toast.error('Please Add Item !', {
        position: "top-center",
      });

    }
  }


  return (
    <div
      className='form' >
     <img src={list} className='photo' alt="" />
    <h3 className='header'> Add Your Item Here</h3>

      <form
        action=""
        onSubmit={Submit}
      >
        <input type="text"
          value={inp}
          onChange={(e) => setinp(e.target.value)}
          placeholder='Add Item ...'
          className='inp_css'
        />

        <button type="submit" className='btn_css' >

          {
            toggle ?
            <FcAddDatabase title='Add Item'  />  : 
            <FcEditImage title='Edit Item'  />
             
          }
          
</button>
      </form>
      <ToastContainer />
    </div>
  )
}

export default Form
