import React, { useReducer } from 'react'
import './index.css'

const initial_state = 0;

const reducer = (state, action) => {

  console.log(state, action)

  switch (action.type) {
    case 'INCREMENT':
return state + 1;
      break;

    case 'DECREMENT':
      return state -1;
      break;

    default: 
    state
      break;
  }

}



const App = () => {

  const [state, dispatch] = useReducer(reducer, initial_state)



  return (
    <div className='app'>
      <h2> {state} </h2>
      <button className='btn_css' onClick={() => dispatch({ type: 'INCREMENT' })}  > Increment </button>
      <button className='btn_css' onClick={() => dispatch({ type: 'DECREMENT' })}    > Decrement </button>
    </div>
  )
}

export default App
