import React,{useState} from 'react'
import Form from './Form'
import List from './List'
import { nanoid } from 'nanoid'
const App = () => {

  const Data = [
{id:nanoid(), item:'Apple'},
{id:nanoid(), item:'Banana'}
  ]

  const [data, setdata] = useState(Data)

  const additem = (inp) => {

    const newitem = {
      id:nanoid(), item:inp
    }
    const itemadded = [{...data,newitem}]
    setdata(itemadded)

  }


  return (
    <div className='app'>
      <Form  additem={additem} />
    
      {
             data.map((item)=>{
              return   <List 
              key={item.id}
              items={item}
              />
             })
      }
    </div>
  )
}

export default App
