import React, { useState, useReducer } from 'react'

const newObj = [ {id:Date.now(),email:'asd@gmail.com',password:123}]

const reducer = (state,action) => {

}

const Reducer = () => {

    const [inp, setinp] = useState({ email: '', pwd: '' })

    const [state, dispatch] = useReducer(reducer,newObj)
     
    const Input = e => {
        const {name,value} = e.target
        setinp({...inp,[name]:value})
    }

    const Submit = e => {
        e.preventDefault()
        setinp({email:'',pwd:''})
    }

    return (
        <div>

            <form action="" onSubmit={Submit}>
                <input
                    name='email'
                    value={inp.email}
                    onChange={Input}
                    className='inp_css'
                    placeholder='Enter Email'
                    type="email"
                />
                <input
                    name='pwd'
                    value={inp.pwd}
                    onChange={Input}
                    className='inp_css'
                    placeholder='Enter Password'
                    type="password"
                />
                <input
                    className='btn_css'
                    type="submit"
                />

            </form>

        </div>
    )
}

export default Reducer
