import React, { useContext } from 'react'
import { createContext } from 'react'
import ComA from './ComA'
import { Properties } from './App'



const Functionality = createContext()



const Main = () => {

const state = useContext(Properties)


const Increment = () => {
   state. setcount(state.count + 1)
  }
  const Decrement = () => {
    state.setcount(state.count - 1)
  }

  return (
    <div>
        <Functionality.Provider value={{increment:Increment, decrement:Decrement}}>
        <ComA/>
        </Functionality.Provider>
    </div>
  )
}

export default Main
