import React, { useState, useReducer } from 'react'

const object =
    [{ id: Date.now(), email: 'adil@gmail.com', password: '12345' }]

const reducer = (state, action) => {
    switch (action.type) {
        case 'add': return [...state, action.payload]
        case 'delete': state.filter((fil)=> fil.id !== action.payload.id)


        default: return state
break;
    }
}

const Reducer = () => {

    const [inp, setinp] = useState({ email: '', pwd: '' })
    const [state, dispatch] = useReducer(reducer, object)

    const Input = e => {
        const { name, value } = e.target
        setinp({ ...inp, [name]: value })

    }

    const Submit = e => {
        e.preventDefault()
        const contact = { id: Date.now(), email: inp.email, password: inp.pwd }
        dispatch({ type: 'add', payload: contact })
        setinp({ email: '', pwd: '' })
    }

    return (
        <div>

            <form action="" onSubmit={Submit}>
                <input
                    name='email'
                    value={inp.email}
                    onChange={Input}
                    className='inp_css'
                    placeholder='Enter Email'
                    type="email"
                />
                <input
                    name='pwd'
                    value={inp.pwd}
                    onChange={Input}
                    className='inp_css'
                    placeholder='Enter Password'
                    type="password"
                />
                <input
                    className='btn_css'
                    type="submit"
                />

            </form>

            <table>
                <tr>
                    <th>Email</th>
                    <th>Password</th>
                    <th>Delete</th>
                </tr>


                {
                    state.map((elem) => {
                        return <tr key={elem.id} >
                            <td> {elem.email} </td>
                            <td> {elem.password} </td>
                            <td>
                                <button
onClick={()=>dispatch({type:'delete',payload:(elem.id)})}
                                    className='btn_css tab_btn'
                                > Delete</button>
                            </td>
                        </tr>
                    }

                    )
                }



            </table>

        </div>
    )
}

export default Reducer
