import React, { useReducer } from 'react'
import { useState } from 'react'

const  newObject =[ { 
    id: Date.now() ,
     email:'asad@gmail.com', 
     password:123 ,
    }]

    const reducer = (state,action) => {
switch (key) {
    case value:
        
        break;

    default:
     throw new Error()
}
    }

const Reducer = () => {

    const [state, dispatch] = useReducer(reducer, newObject )

    const [inp,setinp] = useState({email:'',pwd:''})




    const handle_input = e => {
        const {name,value} = e.target
         setinp({...inp,[name]:value})
    }


    const Submit = e => {
        e.preventDefault()
        setinp({email:'',pwd:''})
        const contact = {
            id:Date.now() ,
            email: inp.email,
            password:inp.pwd
        }

        dispatch({type:'add',payload:contact})
    }

    return (
        <div>
            <form action="" onSubmit={Submit}>
            < input
                className='inp_css'
                onChange={handle_input}
                name='email'
                value={inp.email}
                type="email" />
            < input
                className='inp_css'
                name='pwd'
                onChange={handle_input}
                value={inp.pwd}
                type="password" />
                <input type="submit" className='btn_css' value="Submit" />

</form>
 
{
    state.map( elem => {
        return  <h2 key={elem.id}> {elem.email + " - - - - " + elem.password}  </h2>
       
    })
}

        </div>
    )
}

export default Reducer
