import React, { useReducer } from 'react'
import './index.css'

const initial_state = 0;

const reducer = (state, action) => {



  switch (action.type) {
    case 'INCREMENT':
 return state += 2;


    case 'DECREMENT':
          if (state > 1) {
            return state-=2;  
          } else{
            return  state = 0
          }
      case 'RESET' :
        return state = 0

    default:
      return state

  }

}



const App = () => {

  const [state, dispatch] = useReducer(reducer, initial_state)



  return (
    <div className='app'>
      <h2> {state} </h2>
      <button className='btn_css' onClick={() => dispatch({ type: 'INCREMENT' })}  > Increment </button>
      <button className='btn_css' onClick={() => dispatch({ type: 'DECREMENT' })}    > Decrement </button>
      <button className='btn_css' onClick={() => dispatch({ type: 'RESET' })}    > Reset </button>
    </div>
  )
}

export default App
