import React, { useReducer } from 'react'



const reducer =(state,action) => {

  switch (action.type) {
    case 'inc': return {...state, count:state.count+1}
    case 'dec': return state.count > 1 ? {...state, count:state.count-1}: {count:state.count=0}
    case 'tgl': return {...state, mode:!state.mode}
      case 'handle_input' : return { ...state, input:action.input_val }
   
  
    default: return state 
    
  }

}

const App = () => {

 const [state, dispatch] = useReducer( reducer , {count:0, input:'', mode:false})

  return (
    <div className={`app ${state.mode ? 'color_change' : ''}`}>

      <h1> {state.input} </h1>

      <input
        className='inp_css'
        placeholder='Enter name'
    value={state.input}
    onChange={(e)=>dispatch({type:'handle_input', input_val:e.target.value})}
        type='text'
       />

      <h1> {state.count} </h1>

      <button
        className='btn_css'
        onClick={()=>dispatch({type:'inc'})}
        >
        Increase
      </button>

      <button
        className='btn_css'
        onClick={()=>dispatch({type:'dec'})}
      >
        Decrease
      </button>

      <button
        className='btn_css'
        onClick={()=>dispatch({type:'tgl'})}
        >
        Change Mode
      </button>

    </div>
  )
}

export default App
