import Form from './Form'
import List from './List'
import { nanoid } from 'nanoid'
const App = () => {

// const Array = [
//   // {id:nanoid(),thing:'Banana'}
// ]

  const [data, setdata] = useState([])
  const [inp, setinp] = useState('')
  const [toggle, settoggle] = useState(true)
const [edit, setedit] = useState(null)


  useEffect(() => {
   const fetched = JSON.parse(localStorage.getItem('todo'))  
  setdata(fetched)
  
  }, [])
  

  const additem = (inp) => {
    const newitem = {id:nanoid(),thing:inp}
    const newthing = [...data,newitem]
    localStorage.setItem('todo',JSON.stringify(newthing))
    setdata(newthing)
    if (inp && !toggle) {
      
    }
  }

  const removeitem = (id) => {
    let del_item = JSON.parse(localStorage.getItem('todo'))
     del_item = del_item.filter((del)=> del.id !== id )
     localStorage.setItem('todo',JSON.stringify(del_item))
     setdata(del_item)
  }

  const edititem = (id) => {
 let edit_item = data.find((item)=>{return item.id === id})
console.log(edit_item);
settoggle(false)
setinp(edit_item.thing)
setedit(id)
  }


  return (
    <div className='app'>
      <Form 
       additem={additem} 
       inp={inp}
       setinp={setinp}
       toggle={toggle}
       />
    
      {
             data.map((elem)=>{
              return   <List 
              key={elem.id}
              items={elem}
              removeitem={removeitem}
              edititem={edititem}
              />
             })
      }
    </div>
  )
}

export default App
